using City_Guide.Models;

namespace City_Guide.Controllers
{
    public class EntertainmentController : Controller
    {
        List<EntertainmentModel> entertainment = new List<EntertainmentModel>
            {
                new EntertainmentModel {Id = 1 , name ="King Abdulaziz Center for World Culture (Ithra')",location="8386 Ring Rd, Gharb Al Dhahran. Al Dhahran 34461",image ="https://www.arabnews.com/sites/default/files/styles/n_670_395/public/2020/07/18/2192281-1098022112.jpg?itok=FetoAk-H", description="A project by Saudi Aramco serves as the advancement for the public. Saudi Aramco envisioned Ithra to be an environment for transformative experiences to unlock the power of potential through mastery in arts, science, literature, and innovation. Our facilities include Idea Lab, Library, Ithra Theater, Cinema, Ithra Museum, Children’s Museum, Energy Exhibit, The Great Hall, and the Ithra Tower; all of which are housed to provide engaging and revolutionary experiences.",
                  },
                new EntertainmentModel {Id = 2 , name ="Pavilion Al Khobar",location="King Faisal Bin Abdulaziz Rd. Khobar 34421",image ="https://i.ytimg.com/vi/ur4aRhLtoz4/maxresdefault.jpg", description="An entertainment hub for all Khobar residents and visitors. Its innovative design is goregeous and it hosts many amazing restaurants and activates.",
            },
                new EntertainmentModel {Id = 3 , name ="Ajdan Walk",location="Prince Turki Bin Abdulaziz St. Khobar 34414",image ="https://ajdan.com.sa/wp-content/uploads/2020/06/image-_02-1.jpg", description="Beautifully designed venue by the corniche. Take a walk and enjoy the scenery or dine at the numerous restaurants around.",
                    },
                new EntertainmentModel {Id = 4 , name ="Aramco Beach",location="Halfmoon Bay. Khobar 34754",image ="https://fastly.4sqi.net/img/general/width960/66994482_2vtQZQBBVhpMkmZ6uZAiU2f-QmCnO52DYRXJluwyuE0.jpg", description="Yea its a beach, I hate all bodies of water so I cannot really give an opinion but its there if you want to visit."
                },
                new EntertainmentModel {Id = 5 , name ="Villagio Village",location="King Saud Bin Abdulaziz Rd. (Mushari Bin Abdulaziz St.)",image ="data:image/jpeg;base64,", description="Nice place to take a walk and gaze at the wonders of humans, you can also dine at the many restaurants here or even sing karaoke.",
                    },
                
            };

        public IActionResult Index()
        {
            ViewData["ent"] = entertainment;
            return View();
        }

        public IActionResult Details(int? id)
        {
            EntertainmentModel entertain = entertainment.Find(r => r.Id == id);
            if (entertain == null)
            {
                return Content("No restaurant found");
            }
            else
            {
                ViewData["ent"] = entertain;
                return View();
            }
        }
    }

}
